import React, { Component } from 'react';
import Regular from './Regular.js'
import './Regular.css'


class App extends Component {

    state = {
        fontColor: 'black',
        bgColor: 'white',
    }

    FrogColor = () => {
        var newFont = 'green';
        var newBG = 'green';
        this.setState({
            fontColor: newFont,
        });
        console.log("font: " + this.state.fontColor + " bg: " + this.state.bgColor);
    }

    render() {
        return (
            <div className="App">
                <button className="button" onClick={this.FrogColor}>Frog Mode</button>
                <Regular style={{background: this.state.bgColor}}></Regular>
            </div>
        );
    }
}

export default App;
