import React, { Component } from 'react';
import Regular from './Regular.js'



var fontColor = 'black';
var bgColor = 'white';

let style = {
    background: bgColor,
    font: fontColor,
}

class App extends Component {
    state = {
        fontColor: 'black',
    }

    FrogColor = () => {
        newFont = 'green';
        newBG = 'green';
        this.setState({fontColor: newFont});
        console.log("font: " + fontColor + " bg: " + bgColor);
    }

    render() {
        return (
            <div className="App">
                <button className="button" onClick={this.FrogColor}>Frog Mode</button>
                <Regular style={style} bg={this.state.fontColor}></Regular>
            </div>
        );
    }
}

export default App;
