import React, { Component } from 'react';
import Regular from './Regular.js';
import Projects from './Projects.js';
import StyleOptions from './StyleOptions.js';
import './Regular.css';
import data from './myProjects.json';


let themes = {
    '0' : ['white', 'black'],
    '1' : ['green', 'green'],
    '2' : ['#FFD7F8', '#DCE5FF']
}

class App extends Component {
    constructor(props) {
        super(props)
        this.state = {
            fontColor: 'black',
            bgColor: 'white',
        }
        let myProjects = JSON.parse(JSON.stringify(data));
    }

    setColor = (bg, font) => {
        this.setState({
            fontColor: font,
            bgColor: bg,
        });
    }

    render() {
        return (
            <div>
                <StyleOptions id="options" setColor={this.setColor}
                                themes={themes}></StyleOptions>
                <Regular fontColor={this.state.fontColor}
                        bgColor={this.state.bgColor}
                        setColor={this.setColor}></Regular>
                <div id="projects">
                    <Projects items={this.myProjects}></Projects>
                </div>
            </div>
        );
    }
}

export default App;
