import React, { Component } from 'react';
import './Regular.css';



export default class StyleOptions extends React.Component {
	getColors = () => {
		var e = document.getElementById("options");
		var value = e.options[e.selectedIndex].text;
		alert(value);
	}

	render() {
	    return (
	    	<form>
	    		<select id="options">
		    		<option className="button" value="white black" onClick={() => this.props.SetColor('white', 'black')}>Professional Mode</option>
		    		<option className="button"  value="green green" onClick={() => this.props.SetColor('green', 'green')}>Frog Mode</option>
		    		<option className="button" value="#FFD7F8 #DCE5FF" onClick={() => this.props.SetColor('#FFD7F8', '#DCE5FF')}>Pantone 2017 Mode</option>
		    	</select>
		    	<input type="submit" onClick={() => this.getColors()} />
	    	</form>
	    )
}
}
